func renderNote(note *cbc.Note) string {
	buf := new(bytes.Buffer)
	if err := markdown.Convert([]byte(note.Text), buf); err != nil {
		return fmt.Sprintf("!(ERR %v)", err.Error())
	}
	return buf.String()
}

